<div class="wi-bg-body">
	<div class="container">
	<form>

		<div class="form-group">
			<label class="control-label">Order By</label>
			<select class="form-control">
				<option value="price">Price</option>
				<option value="bedrooms">Bedroom</option>
				<option value="area">Area</option>
			</select>

			<p class="help-block"></p>
		</div>

		<div class="form-group">
			<label class="control-label">City</label>
			<input type="hidden" name="city" value=""><div id="prppropertyfilter-city" class="form-listbox" name="city"><div class="checkbox"><label><input type="checkbox" name="city[]" value="istanbul"> Istanbul</label></div></div>

			<p class="help-block"></p>
		</div>
		<div class="form-group">
			<label class="control-label">Title</label>
			<input type="text" class="form-control" name="title">

			<p class="help-block"></p>
		</div>
		<div class="form-group">
			<label class="control-label">Offer Type</label>
			<input type="hidden" name="offertype" value="">
			<div class="form-listbox offertype" name="offertype">
				<div class="checkbox"><label><input type="checkbox" name="offertype[]" value="1"> For Sale</label></div>
				<div class="checkbox"><label><input type="checkbox" name="offertype[]" value="2"> Sale In Installments</label></div>
				<div class="checkbox"><label><input type="checkbox" name="offertype[]" value="3"> Monthly Rent</label></div>
				<div class="checkbox"><label><input type="checkbox" name="offertype[]" value="4"> Weekly Rent</label></div>
				<div class="checkbox"><label><input type="checkbox" name="offertype[]" value="5"> Daily Rent</label></div>
			</div>

			<p class="help-block help-block-error"></p>
		</div>
		<div class="form-group field-prppropertyfilter-type">
			<label class="control-label">Property Type</label>
			<input type="hidden" name="type" value="">
			<div id="prppropertyfilter-type" class="form-listbox offertype" name="type">
				<div class="checkbox"><label><input type="checkbox" name="type[]" value="1"> Apartment</label></div>
				<div class="checkbox"><label><input type="checkbox" name="type[]" value="2"> Villa</label></div>
				<div class="checkbox"><label><input type="checkbox" name="type[]" value="3"> Land</label></div>
				<div class="checkbox"><label><input type="checkbox" name="type[]" value="4"> Store</label></div>
				<div class="checkbox"><label><input type="checkbox" name="type[]" value="6"> Apartment Complex</label></div>
			</div>

			<p class="help-block help-block-error"></p>
		</div>
		<div class="form-group field-prppropertyfilter-pricerange">
			<label class="control-label" for="prppropertyfilter-pricerange">Price</label>
			<select id="prppropertyfilter-pricerange" class="form-control" name="priceRange" dir="ltr">
				<option value="0"></option>
				<option value="1">&lt; 35,000 (USD)</option>
				<option value="2">35,000 - 55,000 (USD)</option>
				<option value="3">55,000 - 85,000 (USD)</option>
				<option value="4">85,000 - 115,000 (USD)</option>
				<option value="5">&gt; 115,000 (USD)</option>
			</select>

			<p class="help-block help-block-error"></p>
		</div>
		<div class="form-group field-prppropertyfilter-roomrange">
			<label class="control-label" for="prppropertyfilter-roomrange">Rooms</label>
			<select id="prppropertyfilter-roomrange" class="form-control" name="roomRange" dir="ltr">
				<option value="0"></option>
				<option value="1">1 + 1</option>
				<option value="2">2 + 1</option>
				<option value="3">3 + 1</option>
				<option value="4">4 + 1</option>
			</select>

			<p class="help-block help-block-error"></p>
		</div>
		<div class="form-group field-prppropertyfilter-roomrange">
			<label class="control-label" for="prppropertyfilter-roomrange">Message</label>
			<textarea placeholder="Your message" name="name" rows="8" cols="80"></textarea>

			<p class="help-block help-block-error"></p>
		</div>
		<div class="form-group">
			<button type="submit" class="btn btn-primary btn-block"><i class="fa fa-search fa-fw"></i> Search</button>
		</div>

	</form>

</div>

</div>


